import React from 'react';
import './Joke.css';

class Wrapper extends React.Component {
	state = { votes: 0 };

	upvote = () => vote(id, +1);
	downvote = () => vote(id, -1);
	render() {
		return (
			<div>
				{this.props.render({
					upvote   : this.upvote.bind(this),
					downvote : this.downvote.bind(this),
					state    : this.state.votes
				})}
			</div>
		);
	}
}

